    private void btnIngresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIngresarActionPerformed
        // TODO add your handling code here:
        try {
            String u = txtUsuario.getText();
            String p = new String(txtPassword.getPassword());
            Controlador c = new Controlador();
            if (c.verificarUsusario(u, p)) {
                Principal pr = new Principal();
                pr.setVisible(true);
                this.dispose();
            } else {
                JOptionPane.showMessageDialog(this, "USUARIO O CONTRASEÃ‘A INCORRECTA", "Mensaje", JOptionPane.ERROR_MESSAGE);
                txtUsuario.setText("");
                txtPassword.setText("");
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "NO HAY CONEXION A LA BASE  DE DATOS", "Mensaje", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnIngresarActionPerformed

